sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

	var inbound_outbound = g_form.getValue('integration_type.inbound_outbound');
    if (inbound_outbound.indexOf('outbound') > -1)
        script = template_outbound;
    if (inbound_outbound.indexOf('inbound') > -1)
        script = template_inbound;

    g_form.setValue('script', script);
}

var template_outbound = "(function executeRule(fieldMappingGr, triggerRecord) {\r\n	// Add your code here\r\n })(fieldMappingGr, triggerRecord);";
var template_inbound = "(function executeRule(payload,fieldMappingGr, targetRecord) {\r\n	// Add your code here\r\n })(fieldMappingGr, triggerRecord);";
