sys_script_include.script:
var AttachmentUtil = Class.create();
AttachmentUtil.prototype = {
    initialize: function() {},

    /*_________________________________________________________________
       * Description:
       * Parameters:
       * Returns:
       ________________________________________________________________*/
    checkTaskCorrelationId: function(AttachmentGr) {

        var task = new GlideRecord(current.table_name);
        task.get(current.table_sys_id);

		return !gs.nil(task.correlation_id);

    },


    type: 'AttachmentUtil'
};
