sys_ui_action.script:
var options = [];
var choiceList = GlideChoiceList.getChoiceList(current.integration_type.integration_type.table, current.local_field);
for (var i = 0; i < choiceList.getSize(); i++) {
    options.push({
        'value': choiceList.getChoice(i).getValue(),
        'label': choiceList.getChoice(i).getLabel()
    });
}

for(option in options){
    var checkIntegChoice = new GlideRecord('x_324813_ebonding_choice_mapping');
    checkIntegChoice.addQuery('local_choice',options[option].value);
    checkIntegChoice.addQuery('field_mapping',current.getUniqueValue());
    checkIntegChoice.setLimit(1);
    checkIntegChoice.query();
    if(!checkIntegChoice.hasNext()){
        var newIntegChoice = new GlideRecord('x_324813_ebonding_choice_mapping');
        newIntegChoice.newRecord();
        newIntegChoice.field_mapping = current.getUniqueValue();
        newIntegChoice.local_choice = options[option].value;
        newIntegChoice.remote_choice = options[option].value;
        newIntegChoice.insert();
    }

	var checkOutIntegChoice = new GlideRecord('x_324813_ebonding_outbound_choice_mapping');
    checkOutIntegChoice.addQuery('local_choice',options[option].value);
    checkOutIntegChoice.addQuery('field_mapping',current.getUniqueValue());
    checkOutIntegChoice.setLimit(1);
    checkOutIntegChoice.query();
    if(!checkOutIntegChoice.hasNext()){
        var newIntegChoice = new GlideRecord('x_324813_ebonding_outbound_choice_mapping');
        newIntegChoice.newRecord();
        newIntegChoice.field_mapping = current.getUniqueValue();
        newIntegChoice.local_choice = options[option].value;
        newIntegChoice.remote_choice = options[option].value;
        newIntegChoice.insert();
    }
}
