sys_ui_action.script:
doInsertAndStayWithMappings();

function doInsertAndStayWithMappings() {
    var saveMe = current;
    var old_id = current.getValue('sys_id');
    current.sys_scope = gs.getCurrentApplicationId();
    var new_id = current.insert();
    duplicateMappingRecords('x_324813_ebonding_integration_field_mappings', old_id, new_id);
	duplicateRecords('x_324813_ebonding_payload_tranformation_script', old_id, new_id);
    action.setRedirectURL(saveMe);
}

function duplicateMappingRecords(table, old_val, new_val) {
    if (new_val == null || new_val == '')
        return;

    var gr = new GlideRecord(table);
    gr.addQuery('integration_type', old_val);
    gr.query();
    while (gr.next()) {
        var newGR = new GlideRecord(table);
        newGR.get(gr.sys_id);
        newGR.setValue('integration_type', new_val);
        newGR.sys_scope = gs.getCurrentApplicationId();
        var new_id = newGR.insert();
        if (gr.choice_mappings_required == true) {
            var oldChoiceGr = new GlideRecord('x_324813_ebonding_choice_mapping');
            oldChoiceGr.addQuery('field_mapping', gr.sys_id);
            oldChoiceGr.query();
            while (oldChoiceGr.next()) {
                var newChoiceGR = new GlideRecord('x_324813_ebonding_choice_mapping');
                newChoiceGR.get(oldChoiceGr.sys_id);
                newChoiceGR.setValue('field_mapping', new_id);
                newChoiceGR.sys_scope = gs.getCurrentApplicationId();
                newChoiceGR.insert();
            }
        }
    }
}

function duplicateRecords(table, old_val, new_val) {
    if (new_val == null || new_val == '')
        return;

    var gr = new GlideRecord(table);
    gr.addQuery('transaction_type', old_val);
    gr.query();
    while (gr.next()) {
        var newGR = new GlideRecord(table);
        newGR.get(gr.sys_id);
        newGR.setValue('transaction_type', new_val);
        newGR.sys_scope = gs.getCurrentApplicationId();
        newGR.insert();
    }
}
