sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var integType = new GlideRecord('x_324813_ebonding_integration_type');
	integType.addQuery('table','incident');
	integType.setLimit(1);
	integType.query();
	integType.next();

	var output = {};

	for(var field in grJson) {
		var fieldMappings = new GlideRecord('x_324813_ebonding_integration_field_mappings');
		fieldMappings.addQuery('remote_field',field);
		fieldMappings.addQuery('integration_type',integType.getUniqueValue());
		fieldMappings.addQuery('inbound',true);
		fieldMappings.query();
		if(fieldMappings.next()){
			if(fieldMappings.choice_mappings_required == 'true'){
				var choiceMapping = new GlideRecord('x_324813_ebonding_choice_mapping');
				choiceMapping.addQuery('remote_choice',grJson[field]);
				choiceMapping.addQuery('field_mapping',fieldMappings);
				choiceMapping.query();
				if(choiceMapping.next()){
					output[fieldMappings.local_field] = choiceMapping.getValue('local_choice');
				}
			} else{
			output[fieldMappings.local_field] = grJson[field];
			}
		}



}

})(request, response);
